mapper.update:

UPDATE  NOTICE
SET     NOTICE_TITLE = #{NOTICE_TITLE},
        NOTICE_CONTENT = #{NOTICE_CONTENT}
WHERE   NOTICE_NUM = #{NOTICE_NUM}


UPDATE   FILES
SET      FILES_DEL = 'N'
WHERE   FILES_NUM = #{FILE_IDX}


UPDATE  NOTICE
SET     NOTICE_DEL_GB = 'Y'
WHERE   NOTICE_NUM = #{NOTICE_NUM}


UPDATE   FILES
SET      FILES_DEL = 'Y'
WHERE   FILES_PARENT = #{IDX}
